{
    "name": "my_rule_update_tto",
    "description": "la règle qui boucle sur l'agent iot !",
    "text": "select *,\"my_rule_update_tto\" as ruleName, *,cast(cast(ev.temperature?, String),float) + 1.0 as temp from pattern [every ev=iotEvent(cast(cast(temperature?,String),float)<5.0 and type=\"thing\")]",
    "action":{
		"type":"post",
    	"parameters":{
	         "url": "http://idasJSON:7896/iot/json?k=apikey1&i=${id}",
	         "method": "POST",
	         "headers": {
	            "Content-type": "application/json"
	         },
	         "qs": {
	         },
	         "json": {
	            "t": "${temp}",
	            "status": "çà chauffe !!!"
	         }
    	}
    }
}

